export const translate = (str, lang) => {
  const dictionary = {
    'Español': {
      'Español': 'Español',
      'Ingles': 'Spanish',
    },
    'Ingles': {
      'Español': 'Ingles',
      'Ingles': 'English',
    },
    'Idioma': {
      'Español': 'Idioma',
      'Ingles': 'Language',
    }
  }

  return dictionary[str] ? dictionary[str][lang] : str
};
