import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { observer } from 'mobx-react';

import { withStore } from '../../hocs';

import {
  Field,
  TextInput,
  Select,
} from 'shipnow-mercurio'

import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

@observer
class UsersForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      roles: null,
    }
  }

  componentDidMount() {
    this.setState({
      roles: this.props.store.roles.search({}, 'roles-list-users-form', true),
    })    
  }

  render() {
    if (!this.state.roles || !this.state.roles.isOk()) {
      return null;
    }
    return(
      <React.Fragment>
        <Field isHorizontal label="Nombre y Apellido" labelNote="¿Cómo se llama el nuevo usuario?">
          <TextInput placeholder="Nombre"/>
          <TextInput placeholder="Apellido"/>
        </Field>
        <Field label="Email">
          <TextInput />
        </Field>
        <Field label="Rol">
        <Select 
          placeholder="Roles" 
          borderless 
          icon={ faChevronDown } 
          options={ this.state.roles.toArray().map( rol => ({ key: rol.name, value: rol.id })) }
          loading />
        </Field>
      </React.Fragment> )
  }

}

UsersForm.PropTypes = {
  user: PropTypes.object,
}

UsersForm.defaultProps = {
  user: null,
}

export default withStore(UsersForm);
