import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import './styles.css';

import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

import {
  TextInput,
  Panel,
  Dropdown,
  DropdownToggle,
  DropdownPanel,
  Text,
} from 'shipnow-mercurio';

class ProfessionalSuggest extends Component {
  professionals
  constructor(props) {
    super(props);

    this.state = {
      suggest: props.professionals,
      value: '',
      showPanel: false,
    }

    this.professionals = props.professionals;

    this.handleInput = this.handleInput.bind(this);
  }

  handleInput( sender, value, name ) {
    const filtered = value == '' ? this.professionals : this.professionals.filter( professional => professional.fullName.includes(value)) 
    this.setState({
      suggest: filtered,
      value: value,
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.professionals != this.props.professionals) {
      this.setState({
        suggest: this.props.professionals,
      })
    }
  }

  handleSelect( professional ) {
    this.setState({
      value: professional.fullName,
      showPanel: false,
    })
  }

  render() {
    const { disabled } = this.props;
    return(
      <Dropdown className="is-fullwidth" disabled={ disabled }>
        <DropdownToggle className="is-fullwidth" disabled={ disabled }>
          <TextInput
            disabled={ disabled }
            borderless={ !this.state.showPanel }
            placeholder="Profesional"
            className="is-fullwidth" 
            icon={ faChevronDown } 
            value={ this.state.value } 
            onChange={ this.handleInput } 
            onFocus={ () => (this.setState({showPanel: true}))} onBlur={ () => (this.setState({showPanel: false})) }/>
        </DropdownToggle>
          <DropdownPanel>
            { this.state.suggest && this.state.suggest.map( (professional, index) => (
              index < 5 && <Text className="mb-1" size="md" onClick={ () => (this.handleSelect(professional)) }>{ professional.fullName }</Text> )) }
          </DropdownPanel>
      </Dropdown> )
  }


}

ProfessionalSuggest.PropTypes = {
  professionals: PropTypes.array,
  disabled: PropTypes.bool,
}

ProfessionalSuggest.defaultProps = {
  professionals: [],
  disabled: false,
}

export default ProfessionalSuggest
