import React, { Component } from 'react';
import PropTypes from 'prop-types';

import {
  Field,
  Select,
  DateTimePicker
} from 'shipnow-mercurio';

import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

import { withStore } from '../../hocs';

class AppointmentsForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      professionals: null,
    }
  }

  componentDidMount() {
    this.setState({
      professionals: this.props.store.professionals.search({}),
    })
  }


  render() {
    if (!this.state.professionals) {
      return null;
    }
    return(
      <React.Fragment>
        { !this.props.withDate &&
          <Field className="ml-5" label="¿Que día querés venir?" labelNote="Seleccioná ua fecha">
            <DateTimePicker />
          </Field> }
        <Field className="ml-5" label="¿A cual de nuestras sucursales querés venir?" labelNote="Seleccioná una sucursal">
          <Select placeholder="Sucursales" borderless icon={ faChevronDown } options={ [] } />
        </Field>
        <Field className="ml-5" label="¿Por quién querés ser atendido?" labelNote="Seleccioná un profesional">
          <Select placeholder="Profesionales" borderless icon={ faChevronDown } options={ [] } />
        </Field>
        <Field className="ml-5" label="¿Cual de nuestros servicios requeris?" labelNote="Seleccioná un servicio">
          <Select placeholder="Servicios" borderless icon={ faChevronDown } options={ [] } />
        </Field>
        <Field className="ml-5" label="¿A que hora querés venir?" labelNote="Seleccioná un horario">
          <Select placeholder="Horarios" borderless icon={ faChevronDown } options={ [] }/>
        </Field>
      </React.Fragment> )
  }
}

AppointmentsForm.PropTypes = {
  withDate: PropTypes.bool,
}

AppointmentsForm.defaultProps = {
  withDate: false
}

export default withStore(AppointmentsForm);
