import React, { Component } from 'react';

import withStore from '../../hocs/withStore';
import { withToastManager } from 'react-toast-notifications';

import { observer } from 'mobx-react';

import startCase from 'lodash/startCase';

import { faSpinner } from "@fortawesome/free-solid-svg-icons";

import {
  Loader,
  Title,
} from 'shipnow-mercurio';

import {
  Level,
  LevelLeft,
  LevelRight
} from 'bloomer';

@observer
class ClientsList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      clients: null,
      showModal: false,
    }
  }

  componentDidMount() {
    this.setState({
      clients: this.props.store.clients.search({}, 'client-list-view', true),
    });    
  }

  renderTable() {
    return null;
  }

  renderModal() {
    return null;
  }

  render() {
    const isClientsLoaded = this.state.clients && this.state.clients.isOk();
    if (!isClientsLoaded) {
      return <Loader icon={ faSpinner } label="Cargando los clientes.." className="fullscreen" />
    }
    return(
      <React.Fragment>
        <Level>
          <LevelLeft>
            <Title>Listado de Clientes</Title>
          </LevelLeft>
        </Level>
        <hr/>
        { this.renderTable() }
        { this.state.showModal && this.renderModal() }
      </React.Fragment> )
  }
}

export default withToastManager(withStore(ClientsList));
