import React, { Component } from 'react';
import { 
    Panel, 
    Text,
    Button,
} from 'shipnow-mercurio';
import moment from 'moment';
import {
    Columns,
    Column,
    Level,
    LevelLeft,
    LevelRight
} from 'bloomer';

import { AppointmentCard } from '../../components/Appointmens';

import startCase from 'lodash/startCase';

class AppointmentsList extends Component {
    constructor(props) {
        super(props);

        this.getDatesInMonth = this.getDatesInMonth.bind(this);
        this.chunk = this.chunk.bind(this);
        this.handleMonth = this.handleMonth.bind(this);

        const date = moment()
        const datesInMonth = this.getDatesInMonth(date)

        this.setState({
            date: date,
            datesInWeeks: this.chunk(datesInMonth, 7)

        })
        debugger
    }

    handleMonth( sender, value, name ) {
        const datesInMonth = this.getDatesInMonth(value);
        this.setState({
            date: value,
            datesInWeeks: this.chunk(datesInMonth, 7)

        })
    }
    /** 
    * chunk => method that "cuts" the array into n arrays of size you want
    */
    chunk( arr, size ) {
        return arr.reduce((acc, _, i) => (i % size) ? acc : [...acc, arr.slice(i, i + size)], []);
    } 

    getDatesInMonth( date ) {
        return Array.from({ length: date.daysInMonth() }, (x, i) => moment().startOf('month').add(i, 'days'))
    }

    render() {
        if (!this.state.datesInWeeks) return null
        const { datesInWeeks } = this.state;
        return (
            <React.Fragment key={ this.state.date }>
                <br />
                <br />
                <Level className="pl-3 pr-3">
                    <LevelLeft>
                        <Button onClick={ this.handleMonth } value={ moment(this.state.date).subtract(1, 'months') } kind="outline">{ `${ startCase(moment(this.state.date).subtract(1, 'months').format('MMMM')) }` }</Button>
                    </LevelLeft>
                    <LevelRight>
                        <Button onClick={ this.handleMonth } value={ moment(this.state.date).add(1, 'months') } kind="outline">{ `${ startCase(moment(this.state.date).add(1, 'months').format('MMMM')) }` }</Button>
                    </LevelRight>
                </Level>
                {datesInWeeks.map(week => (
                    <Columns className="ml-2">
                        {week.map((day, index) => ( <AppointmentCard date={ day }/> ))}
                    </Columns>))}
            </React.Fragment>)
    }
}

export default AppointmentsList;
