import React, { Component } from 'react';
import { 
    Panel, 
    Text,
    Button,
} from 'shipnow-mercurio';
import moment from 'moment';
import {
    Columns,
    Column,
    Level,
    LevelLeft,
    LevelRight
} from 'bloomer';

class AppointmentsList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            date: moment(),
        }
    }


    render() {
        const chunk = (arr, size) => arr.reduce((acc, _, i) => (i % size) ? acc : [...acc, arr.slice(i, i + size)], []);
        const datesInMonth = Array.from({ length: this.state.date.daysInMonth() }, (x, i) => moment().startOf('month').add(i, 'days'));
        const datesInWeeks = chunk(datesInMonth, 7);
        debugger
        return (
            <React.Fragment>
                <br />
                <br />
                <Level>
                    <LevelLeft>
                        <Button kind="outline">Boton</Button>
                    </LevelLeft>
                    <LevelRight>
                        <Button kind="outline">Boton</Button>
                    </LevelRight>
                </Level>
                {datesInWeeks.map(week => (
                    <Columns style={{ marginLeft: '16px' }}>
                        {week.map(day => (
                            <Panel style={{ width: '147px', height: '147px', margin: '8px', padding: '2px' }}>
                                <Text>{`${day.format('D')} - ${day.format('ddd')}`}</Text>
                            </Panel>))}
                    </Columns>))}
            </React.Fragment>)
    }
}

export default AppointmentsList;
