import React, { Component } from 'react';
import { 
    Button,
} from 'shipnow-mercurio';
import moment from 'moment';
import {
    Columns,
    Column,
    Level,
    LevelLeft,
    LevelRight
} from 'bloomer';

import { AppointmentCalendar } from '../../components/Appointmens';

import startCase from 'lodash/startCase';

class AppointmentsList extends Component {
    constructor(props) {
        super(props);

        this.getDatesInMonth = this.getDatesInMonth.bind(this);
        this.chunk = this.chunk.bind(this);
        this.handleMonth = this.handleMonth.bind(this);

    }


    componentDidMount() {
        const date = moment()
        const datesInMonth = this.getDatesInMonth(date)

        this.setState({
            date: date,
            datesInWeeks: this.chunk(datesInMonth, 7)

        })
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.state && prevState && prevState.date != this.state.date) {
            const datesInMonth = this.getDatesInMonth(this.state.date);
            this.setState({
                datesInWeeks: this.chunk(datesInMonth, 7)
            })
        }
    }


    handleMonth( sender, value, name ) {
        this.setState({
            date: value,
        })
    }
    /** 
    * chunk => method that "cuts" the array into n arrays of size you want
    */
    chunk( arr, size ) {
        return arr.reduce((acc, _, i) => (i % size) ? acc : [...acc, arr.slice(i, i + size)], []);
    } 

    getDatesInMonth( date ) {
        return Array.from({ length: date.daysInMonth() }, (x, i) => moment().startOf('month').add(i, 'days'))
    }

    render() {
        if (!this.state) return null
        return (
            <React.Fragment key={ this.state.datesInWeeks }>
                <br />
                <br />
                <Level className="pl-3 pr-3">
                    <LevelLeft>
                        <Button key={ this.state.date } onClick={ this.handleMonth } value={ moment(this.state.date).subtract(1, 'months') } kind="outline">{ `${ startCase(moment(this.state.date).subtract(1, 'months').format('MMMM')) }` }</Button>
                    </LevelLeft>
                    <LevelRight>
                        <Button key={ this.state.date } onClick={ this.handleMonth } value={ moment(this.state.date).add(1, 'months') } kind="outline">{ `${ startCase(moment(this.state.date).add(1, 'months').format('MMMM')) }` }</Button>
                    </LevelRight>
                </Level>
                <AppointmentCalendar key={ this.state.datesInWeeks } weeks={ this.state.datesInWeeks }/>
            </React.Fragment>)
    }
}

export default AppointmentsList;
