import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './styles.css';

import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

import {
  TextInput,
  Panel,
  DropdownPanel,
  Text,
} from 'shipnow-mercurio';

class ClientSuggest extends Component {
  clients
  constructor(props) {
    super(props);

    this.state = {
      suggest: props.clients,
      value: '',
      showPanel: false,
    }

    this.clients = props.clients;

    this.handleInput = this.handleInput.bind(this);
  }

  handleInput( sender, value, name ) {
    const filtered = value == '' ? this.clients : this.clients.filter( client => client.fullName.includes(value)) 
    this.setState({
      suggest: filtered,
      value: value,
    })
  }

  handleSelect( client ) {
    this.setState({
      value: client.fullName,
      showPanel: false,
    })
  }

  render() {
    const dropdownStyle = {
      position: 'absolute',
      zIndex: '20',
      marginTop: '2px',
      backgroundColor: '#FFFFFF',
      width: '100%'
    }
    return(
      <div className="input-suggest" style={{ position: 'relative' }}>
        <TextInput
          className="is-fullwidth" 
          icon={ faChevronDown } 
          value={ this.state.value } 
          onChange={ this.handleInput } 
          onFocus={ () => (this.setState({showPanel: true})) }/>
        { this.state.showPanel &&
          <DropdownPanel style={ dropdownStyle }>
            { this.state.suggest.map( client => (
              <Text className="mb-1" size="md" onClick={ () => (this.handleSelect(client)) }>{ client.fullName }</Text> )) }
          </DropdownPanel>  }
      </div> )
  }


}

ClientSuggest.PropTypes = {
  clients: PropTypes.array,
}

ClientSuggest.defaultProps = {
  clients: [],
}

export default ClientSuggest
