import React, { Component } from 'react';

import withStore from '../../hocs/withStore';
import { withToastManager } from 'react-toast-notifications';

import { observer } from 'mobx-react';

import {
  Loader,
  Title,
  Text,
  Table,
  Button,
  SelectableIcon,
} from 'shipnow-mercurio';

import {
  Level,
  LevelLeft,
  LevelRight
} from 'bloomer';

import { translate } from '../../lib/Translator';

@observer
class BranchesList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      branches: null,
      showModal: false,
    }
  }

  componentDidMount() {
    this.setState({
      branches: this.props.store.branches.search({}, 'branches-list', true),
    })
  }

  getText( text ) {
    return translate(text, this.props.store.ui.language)
  }

  render() {
    const isBranchesLoaded = this.state.branches && this.state.branches.isOk();
    return(
      <React.Fragment>
        <Level>
          <LevelLeft>
            <Title>{ this.getText('Listado de sucursales') }</Title>
          </LevelLeft>
        </Level>
        <hr/>
        { this.renderTable() }
        { this.state.showModal && this.renderModal() }
      </React.Fragment> )
  }
}

export default withToastManager(withStore(BranchesList));
