import React, { Component } from 'react';
import {
  Button,
  Text,
} from 'shipnow-mercurio';
import {
  Columns,
  Column,
} from 'bloomer';
import moment from 'moment';

import { AppointmentCalendar } from '../../components/Appointments';

import startCase from 'lodash/startCase';

class AppointmentsList extends Component {
  constructor(props) {
    super(props);

    this.getDatesInMonth = this.getDatesInMonth.bind(this);
    this.chunk = this.chunk.bind(this);
    this.handleMonth = this.handleMonth.bind(this);

  }


  componentDidMount() {
    const date = moment()
    const datesInMonth = this.getDatesInMonth(date)

    this.setState({
      date: date,
      datesInWeeks: this.chunk(datesInMonth, 7)

    })
  }

  handleMonth(sender, value, name) {
      let newDate = name == 'next' ? moment(this.state.date).add(1, 'months') : moment(this.state.date).subtract(1, 'months')
      const datesInMonth = this.getDatesInMonth(newDate);
      this.setState({
        date: newDate,
        datesInWeeks: this.chunk(datesInMonth, 7)
      })
    }
    /** 
     * chunk => method that "cuts" the array into n arrays of size you want
     */
  chunk(arr, size) {
    return arr.reduce((acc, _, i) => (i % size) ? acc : [...acc, arr.slice(i, i + size)], []);
  }

  getDatesInMonth(date) {
    return Array.from({ length: date.daysInMonth() }, (x, i) => moment(date).startOf('month').add(i, 'days'))
  }

  render() {
    if (!this.state) return null
    return ( <
      React.Fragment key = { this.state.datesInWeeks } >
      <
      br / >
      <
      br / >
      <
      Columns className = "pl-3 pr-4"
      isVCentered >
      <
      Column className = "has-text-left" >
      <
      Button onClick = { this.handleMonth }
      name = "prev"
      kind = "outline" > { `${ startCase(moment(this.state.date).subtract(1, 'months').format('MMMM')) }` } < /Button> <
      /Column> <
      Column className = "has-text-centered" >
      <
      Text weight = "medium"
      size = "xl"
      color = "primaryDark" > { startCase(this.state.date.format('MMMM')) } < /Text> <
      /Column> <
      Column className = "has-text-right"
      style = {
        { paddingRight: '2px' } } >
      <
      Button onClick = { this.handleMonth }
      name = "next"
      kind = "outline" > { `${ startCase(moment(this.state.date).add(1, 'months').format('MMMM')) }` } < /Button> <
      /Column> <
      /Columns> <
      AppointmentCalendar key = { this.state.datesInWeeks }
      weeks = { this.state.datesInWeeks }
      /> <
      /React.Fragment>)
    }
  }

  export default AppointmentsList;
