import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './styles.css'

import {
  Panel,
  Text,
  Button,
} from 'shipnow-mercurio';

import {
  Level,
  LevelLeft,
  LevelRight
} from 'bloomer';

import { AppointmentModal } from './'

import startCase from 'lodash/startCase';

class AppointmentCard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      date: this.props.date,
      reload: false,
      showModal: false
    }

    this.handleClick = this.handleClick.bind(this);
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.date != this.props.date) {
      this.setState(prevState => ({
        reload: !prevState
      }))
    }
  }

  handleClick() {
    this.setState(prevState => ({
      showModal: !prevState
    }))
  }


  render() {
    return(
      <Panel className="appointment_card" key={ this.state.date }>
        <Level>
          <LevelLeft>
            <Text key={ this.state.reload } weight="medium" color="primaryDark">{`${ startCase(this.state.date.format('ddd')) } ${ this.state.date.format('D') }`}</Text>
          </LevelLeft>
          <LevelRight>
            <Text className="appointment_card_see_more mr-1" onClick={ this.handleClick }>Ver mas</Text>
          </LevelRight>
        </Level>
      </Panel> )
  } 
}

AppointmentCard.PropTypes = {
  date: PropTypes.object,
}

AppointmentCard.defaultProps = {
  date: null
}

export default AppointmentCard;
