import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

import { 
  Button, 
  Dropdown, 
  DropdownPanel,
  DropdownMenu, 
  DropdownToggle,
  DropdownMenuItem, 
} from 'shipnow-mercurio'; 

import {
  Checkbox
} from 'bloomer';

class DaysSelector extends Component {
  constructor(props) {
    super(props);

    this.handleDaysChange = this.handleDaysChange.bind(this);
  }

  handleDaysChange( day ) {
    this.props.handleDays && this.props.handleDays(day);
  }

  render() {
    const { defaultProfessional } = this.props;
    const days=['MONDAY','TUESDAY','WEDNESDAY','THURSDAY','FRIDAY','SATURDAY','SUNDAY'];
    const translatedDays = {
      'MONDAY': 'Lunes',
      'TUESDAY': 'Martes',
      'WEDNESDAY': 'Miércoles',
      'THURSDAY': 'Jueves',
      'FRIDAY': 'Viernes',
      'SATURDAY': 'Sábado',
      'SUNDAY': 'Domingo'
    }
    return(
      <Dropdown direction="bottom-left">
          <DropdownToggle>
            <Button icon={ faChevronDown } kind="link" invert >{ this.getSelectedDays() }</Button>
          </DropdownToggle>
          <DropdownPanel>
            {days.map(day => (
              <Checkbox 
                className="pr-1 pb-2" 
                name="day" 
                isFullWidth 
                defaultChecked={ defaultProfessional && defaultProfessional.cookedWorkingDays.includes(translatedDays[day])} 
                onClick={() => this.handleDaysChange(day)} >
                  <Text className="pl-1">{translatedDays[day]}</Text>
                </Checkbox>
            ))}
          </DropdownPanel> 
        </Dropdown> )

  }

}

DaysSelector.PropTypes = {
  defaultProfessional: PropTypes.object,
  handleDays: PropTypes.func,
}

DaysSelector.defaultProps = {
  defaultProfessional: null,
  handleDays: null,
}

export default DaysSelector;
