import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { observer } from 'mobx-react';

import { withStore } from '../../hocs';

import {
  Columns,
  Column,
  Checkbox
} from 'bloomer';

import {
  Text,
  TextInput,
  Field
} from 'shipnow-mercurio';

@observer
class ClientsForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      reload: false,
    }

    this.handleChange = this.handleChange.bind(this);
  }

  handleCategory( category ) {
    this.props.onChange && this.props.onChange('status', category);
    this.setState( prevState => ({
      reload: !prevState.reload 
    }))
  }

  handleChange( sender, value, name) {
    this.props.onChange && this.props.onChange(name, value)
  }

  render() {
    const { client } = this.props
    return(
      <React.Fragment>
        <Field label="Nombre" labelNote="¿Cómo se llama el nuevo cliente?">
          <TextInput name="name" value={ client.name } className="is-fullwidth" />
        </Field>
        <Field label="Documento" labelNote="¿Cuál es el dni del nuevo cliente?">
          <TextInput name="DNI" value={ client.DNI } className="is-fullwidth" />
        </Field>
        <Field label="Teléfono principal" labelNote="¿Cuál es el nro de teléfono del cliente?">
          <TextInput name="primaryPhone" value={ client.primaryPhone } className="is-fullwidth" />
        </Field>
        <Field label="Teléfono secundario" labelNote="Es necesario una segunda opción de comunicación">
          <TextInput name="secondPhone" value={ client.secondPhone } className="is-fullwidth" />
        </Field>
        <Field label="Categoría" labelNote="¿Dentro de qué categoría se encuentra el cliente?">
          <Checkbox 
            className="mt-1"
            isFullWidth 
            defaultChecked={ client.status.name ? client.status.name == 'NORMAL' : client.status == 'NORMAL' } 
            onClick={() => this.handleCategory('NORMAL')}>
              <Text className="ml-1" size="md" weight="medium">Normal</Text>
            </Checkbox>
            <Checkbox 
            className="mt-1" 
            isFullWidth 
            defaultChecked={ client.status.name ? client.status.name == 'MOROSO' : client.status == 'MOROSO' } 
            onClick={() => this.handleCategory('MOROSO')}>
              <Text className="ml-1" size="md" weight="medium">Moroso</Text>
            </Checkbox>
            <Checkbox  
            className="mt-1" 
            isFullWidth 
            defaultChecked={ client.status.name ? client.status.name == 'VIP' : client.status == 'VIP' } 
            onClick={() => this.handleCategory('VIP')}>
              <Text className="ml-1" size="md" weight="medium">VIP</Text>
            </Checkbox>
        </Field>
      </React.Fragment> );
  }
}

ClientsForm.PropTypes = {
  client: PropTypes.object,
  onChange: PropTypes.func,
}

ClientsForm.defaultProps = {
  client: null,
  onChange: null,
}

export default ClientsForm;
